create table taxa (id bigint generated by default as identity, dias_maximo bigint, dias_minimo bigint, percentual numeric(19,2), valor_fixo numeric(19,2), primary key (id));
create table transferencia (id bigint generated by default as identity, conta_destino varchar(255) not null, conta_origem varchar(255) not null, data_agendamento date not null, data_transferencia date not null, taxa numeric(19,2) not null, valor numeric(19,2) not null, primary key (id));