create table if not exists taxa (id bigint generated by default as identity, dias_maximo bigint not null, dias_minimo bigint not null, percentual numeric(19,2) not null, valor_fixo numeric(19,2) not null, primary key (id));
create table if not exists transferencia (id bigint generated by default as identity, conta_destino varchar(255) not null, conta_origem varchar(255) not null, data_agendamento date not null, data_transferencia date not null, taxa numeric(19,2) not null, valor numeric(19,2) not null, primary key (id));
